overlays/modal/modal-nested-demo.raw:
"use client"

import React from "react"

import { toast } from "sonner"
import { Button, Form, Modal, Textarea } from "ui"

export default function ModalNestedDemo() {
  const [isRegistrationModalOpen, setIsRegistrationModalOpen] = React.useState(false)
  const [isProfileSetupModalOpen, setIsProfileSetupModalOpen] = React.useState(false)
  const [isTyping, setIsTyping] = React.useState(false)

  return (
    <>
      <Button onPress={() => setIsRegistrationModalOpen(true)}>Register</Button>

      <Modal.Content
        isOpen={isRegistrationModalOpen}
        onOpenChange={() => setIsRegistrationModalOpen(false)}
        aria-label="Confirm Registration"
      >
        <Modal.Header>
          <Modal.Title>Confirm Registration</Modal.Title>
          <Modal.Description>Please confirm your registration details.</Modal.Description>
        </Modal.Header>
        <Modal.Footer>
          <Modal.Close>Cancel</Modal.Close>
          <Button
            onPress={() => {
              setIsProfileSetupModalOpen(true)
            }}
          >
            Confirm
          </Button>
        </Modal.Footer>
      </Modal.Content>

      <Modal.Content
        isOpen={isProfileSetupModalOpen}
        onOpenChange={(isOpen) => {
          if (!isOpen && isTyping) {
            toast("Profile setup incomplete")
          }
          setIsProfileSetupModalOpen(isOpen)
        }}
        aria-label="Profile Setup"
      >
        <Modal.Header>
          <Modal.Title>Set Up Your Profile</Modal.Title>
          <Modal.Description>
            We need a bit more information before you can get started.
          </Modal.Description>
        </Modal.Header>
        <Form
          onSubmit={(e) => {
            e.preventDefault()
            toast.success("Profile setup complete")
            setIsProfileSetupModalOpen(false)
            setIsRegistrationModalOpen(false)
          }}
        >
          <Modal.Body className="space-y-4">
            <Textarea
              isRequired
              label="Bio"
              placeholder="Tell us something about yourself"
              onInput={() => setIsTyping(true)}
            />
          </Modal.Body>
          <Modal.Footer>
            <Modal.Close>Skip for now</Modal.Close>
            <Button type="submit">Complete Setup</Button>
          </Modal.Footer>
        </Form>
      </Modal.Content>
    </>
  )
}

overlays/sheet/sheet-position-demo.raw:
"use client"

import React from "react"

import { Button, Sheet } from "ui"
import { titleCase } from "usemods"

type Side = "left" | "right" | "top" | "bottom"
export default function SheetPositionDemo() {
  const [sheetSide, setSheetSide] = React.useState<Side>("left")
  const [isOpen, setIsOpen] = React.useState(false)

  const sides: Side[] = ["left", "right", "top", "bottom"]

  const pressHandler = (side: Side, open: boolean) => {
    setSheetSide(side)
    setIsOpen(open)
  }

  return (
    <>
      <div className="grid grid-cols-2 gap-2">
        {sides.map((side, idx) => (
          <Button appearance="outline" onPress={() => pressHandler(side, true)} key={idx}>
            {titleCase(side)}
          </Button>
        ))}
      </div>
      <Sheet.Content isOpen={isOpen} onOpenChange={setIsOpen} side={sheetSide}>
        <Sheet.Header>
          <Sheet.Title>{titleCase(sheetSide)}</Sheet.Title>
          <Sheet.Description>The sheet will go from {sheetSide} side.</Sheet.Description>
        </Sheet.Header>
      </Sheet.Content>
    </>
  )
}

