tag-field.raw:
"use client"

import * as React from "react"

import type { Key } from "react-aria-components"
import { Group, TextField } from "react-aria-components"
import type { ListData } from "react-stately"
import { twJoin } from "tailwind-merge"
import { tv } from "tailwind-variants"

import type { FieldProps } from "./field"
import { Description, Input, Label } from "./field"
import { cn } from "./primitive"
import type { RestrictedIntent, TagGroupProps } from "./tag-group"
import { Tag } from "./tag-group"

const tagFieldsStyles = tv({
  base: ["relative flex min-h-10 bg-bg flex-row flex-wrap items-center"],
  variants: {
    appearance: {
      outline: [
        "px-1 rounded-lg shadow-sm border",
        "has-[input[data-focused=true]]:border-primary",
        "has-[input[data-invalid=true][data-focused=true]]:border-danger has-[input[data-invalid=true]]:border-danger has-[input[data-invalid=true]]:ring-danger/20",
        "has-[input[data-focused=true]]:ring-4 has-[input[data-focused=true]]:ring-primary/20"
      ],
      plain: ["has-[input[data-focused=true]]:border-transparent"]
    }
  }
})

interface TagItemProps {
  id: number
  name: string
}

interface TagFieldProps extends Pick<TagGroupProps, "shape">, FieldProps {
  intent?: RestrictedIntent
  isDisabled?: boolean
  max?: number
  className?: string
  children?: React.ReactNode
  name?: string
  list: ListData<TagItemProps>
  onItemInserted?: (tag: TagItemProps) => void
  onItemCleared?: (tag: TagItemProps | undefined) => void
  appearance?: "outline" | "plain"
}

const TagField = ({
  appearance = "outline",
  name,
  className,
  list,
  onItemCleared,
  onItemInserted,
  ...props
}: TagFieldProps) => {
  const [isInvalid, setIsInvalid] = React.useState(false)
  const [inputValue, setInputValue] = React.useState("")

  const existingTagCount = list.items.length
  const maxTags = props.max !== undefined ? props.max : Infinity
  const maxTagsToAdd = maxTags - existingTagCount

  const insertTag = () => {
    const tagNames = inputValue.split(/,/)
    if (maxTagsToAdd <= 0) {
      setIsInvalid(true)
      setInputValue("")
      const timeoutId = setTimeout(() => {
        setIsInvalid(false)
      }, 2000)

      return () => clearTimeout(timeoutId)
    }

    tagNames.slice(0, maxTagsToAdd).forEach((tagName) => {
      const formattedName = tagName
        .trim()
        .replace(/\s+/g, " ")
        .replace(/[\t\r\n]/g, "")

      if (
        formattedName &&
        !list.items.some(({ name }) => name.toLowerCase() === formattedName.toLowerCase())
      ) {
        const tag = {
          id: (list.items.at(-1)?.id ?? 0) + 1,
          name: formattedName
        }

        list.append(tag)
        onItemInserted?.(tag)
      }
    })

    setInputValue("")
  }

  const clearInvalidFeedback = () => {
    if (maxTags - list.items.length <= maxTagsToAdd) {
      setIsInvalid(false)
    }
  }

  const onRemove = (keys: Set<Key>) => {
    list.remove(...keys)
    onItemCleared?.(list.getItem([...keys][0]))
    clearInvalidFeedback()
  }

  const onKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" || e.key === ",") {
      e.preventDefault()
      insertTag()
    }

    if (e.key === "Backspace" && inputValue === "") {
      popLast()
      clearInvalidFeedback()
    }
  }

  const popLast = React.useCallback(() => {
    if (list.items.length == 0) {
      return
    }

    const endKey = list.items[list.items.length - 1]

    if (endKey !== null) {
      list.remove(endKey.id)
      onItemCleared?.(list.getItem(endKey.id))
    }
  }, [list, onItemCleared])

  return (
    <div className={cn("flex flex-col gap-y-1.5 w-full", className)}>
      {props.label && <Label>{props.label}</Label>}
      <Group className={twJoin("flex flex-col", props.isDisabled && "opacity-50")}>
        <Tag.Group
          intent={props.intent}
          shape={props.shape}
          aria-label="List item inserted"
          onRemove={onRemove}
        >
          <div className={tagFieldsStyles({ appearance })}>
            <div className="flex flex-1 flex-wrap items-center">
              <Tag.List
                items={list.items}
                className={twJoin(
                  list.items.length !== 0
                    ? appearance === "outline" && "py-1.5 px-0.5 gap-1.5"
                    : "gap-0",
                  props.shape === "square" && "[&_.jdt3lr2x]:rounded-[calc(var(--radius)-4px)]",
                  "[&_.jdt3lr2x]:cursor-default last:[&_.jdt3lr2x]:-mr-1 outline-none"
                )}
              >
                {(item) => <Tag.Item>{item.name}</Tag.Item>}
              </Tag.List>
              <TextField
                isDisabled={props.isDisabled}
                aria-label={props?.label ?? (props["aria-label"] || props.placeholder)}
                isInvalid={isInvalid}
                onKeyDown={onKeyDown}
                onChange={setInputValue}
                value={inputValue}
                {...props}
              >
                <Input
                  className="w-auto inline"
                  placeholder={maxTagsToAdd <= 0 ? "Remove one to add more" : props.placeholder}
                />
              </TextField>
            </div>
          </div>
        </Tag.Group>
        {name && (
          <input hidden name={name} value={list.items.map((i) => i.name).join(",")} readOnly />
        )}
      </Group>
      {props.description && <Description>{props.description}</Description>}
    </div>
  )
}

export { TagField, type TagItemProps }

navbar.raw:
"use client"

import * as React from "react"

import { LayoutGroup, motion } from "framer-motion"
import { IconHamburger } from "justd-icons"
import type { LinkProps } from "react-aria-components"
import { Link } from "react-aria-components"
import { tv } from "tailwind-variants"

import { Button } from "./button"
import { cn, cr, useMediaQuery } from "./primitive"
import { Sheet } from "./sheet"

type NavbarOptions = {
  side?: "left" | "right"
  isSticky?: boolean
  intent?: "navbar" | "floating" | "inset"
}

type NavbarContextProps = {
  open: boolean
  setOpen: (open: boolean) => void
  isCompact: boolean
  toggleNavbar: () => void
} & NavbarOptions

const NavbarContext = React.createContext<NavbarContextProps | null>(null)

function useNavbar() {
  const context = React.useContext(NavbarContext)
  if (!context) {
    throw new Error("useNavbar must be used within a Navbar.")
  }

  return context
}

interface NavbarProviderProps extends React.ComponentProps<"header">, NavbarOptions {
  defaultOpen?: boolean
  isOpen?: boolean
  onOpenChange?: (open: boolean) => void
}

const navbarStyles = tv({
  base: "relative isolate flex w-full min-h-svh flex-col",
  variants: {
    intent: {
      floating: "pt-2 px-2.5",
      navbar: "",
      inset: "bg-secondary dark:bg-bg"
    }
  }
})

const Navbar = ({
  children,
  isOpen: openProp,
  onOpenChange: setOpenProp,
  defaultOpen = false,
  className,
  side = "left",
  isSticky = false,
  intent = "navbar",
  ...props
}: NavbarProviderProps) => {
  const isCompact = useMediaQuery("(max-width: 1023px)")
  const [_open, _setOpen] = React.useState(defaultOpen)
  const open = openProp ?? _open

  const setOpen = React.useCallback(
    (value: boolean | ((value: boolean) => boolean)) => {
      if (setOpenProp) {
        return setOpenProp?.(typeof value === "function" ? value(open) : value)
      }

      _setOpen(value)
    },
    [setOpenProp, open]
  )

  const toggleNavbar = React.useCallback(() => {
    setOpen((open) => !open)
  }, [setOpen])

  const contextValue = React.useMemo<NavbarContextProps>(
    () => ({
      open,
      setOpen,
      isCompact,
      toggleNavbar,
      intent,
      isSticky,
      side
    }),
    [open, setOpen, isCompact, toggleNavbar, intent, isSticky, side]
  )
  return (
    <NavbarContext.Provider value={contextValue}>
      <header data-intent={intent} className={navbarStyles({ intent, className })} {...props}>
        {children}
      </header>
    </NavbarContext.Provider>
  )
}

const navStyles = tv({
  base: [
    "hidden h-[--navbar-height] [--navbar-height:3.5rem] px-4 group peer lg:flex items-center w-full",
    "[&>div]:max-w-[1680px] lg:[&>div]:flex [&>div]:items-center [&>div]:w-full [&>div]:mx-auto"
  ],
  variants: {
    isSticky: {
      true: "sticky z-40 top-0"
    },
    intent: {
      floating:
        "bg-tertiary w-full max-w-7xl 2xl:max-w-screen-2xl mx-auto shadow-sm border rounded-xl sm:px-4",
      navbar: "bg-tertiary shadow-sm border-b sm:px-6",
      inset: [
        "bg-secondary mx-auto dark:bg-bg sm:px-6",
        "2xl:[&>div]:max-w-screen-2xl lg:[&>div]:flex [&>div]:items-center [&>div]:w-full [&>div]:mx-auto"
      ]
    }
  }
})

interface NavbarProps extends React.ComponentProps<"div"> {
  intent?: "navbar" | "floating" | "inset"
  isSticky?: boolean
  side?: "left" | "right"
}

const Nav = ({ className, ...props }: NavbarProps) => {
  const { isCompact, side, intent, isSticky, open, setOpen } = useNavbar()

  if (isCompact) {
    return (
      <Sheet isOpen={open} onOpenChange={setOpen} {...props}>
        <Sheet.Content
          side={side}
          aria-label="Compact Navbar"
          data-navbar="compact"
          classNames={{
            content: "text-fg [&>button]:hidden"
          }}
          isStack={intent === "floating"}
        >
          <Sheet.Body className="sm:px-4 px-2">{props.children}</Sheet.Body>
        </Sheet.Content>
      </Sheet>
    )
  }

  return (
    <div className={navStyles({ isSticky, intent, className })} {...props}>
      <div>{props.children}</div>
    </div>
  )
}

const Trigger = ({ className, onPress, ...props }: React.ComponentProps<typeof Button>) => {
  const { toggleNavbar } = useNavbar()
  return (
    <Button
      data-sidebar="trigger"
      appearance="plain"
      aria-label={props["aria-label"] || "Toggle Navbar"}
      size="square-petite"
      className={className}
      onPress={(event) => {
        onPress?.(event)
        toggleNavbar()
      }}
      {...props}
    >
      <IconHamburger />
      <span className="sr-only">Toggle Navbar</span>
    </Button>
  )
}

const Section = ({ className, ...props }: React.ComponentProps<"div">) => {
  const { isCompact } = useNavbar()
  const id = React.useId()
  return (
    <LayoutGroup id={id}>
      <div
        data-slot="navbar-section"
        className={cn(
          "flex",
          isCompact ? "flex-col gap-y-4" : "flex-row gap-x-3 items-center",
          className
        )}
        {...props}
      >
        {props.children}
      </div>
    </LayoutGroup>
  )
}

const navItemStyles = tv({
  base: [
    "relative cursor-pointer lg:text-sm px-2 flex forced-colors:outline-0 items-center gap-x-2 [&>[data-slot=icon]]:-mx-0.5 text-muted-fg outline-none forced-colors:disabled:text-[GrayText] forced-colors:transform-none transition-colors",
    "hover:text-fg focus:text-fg pressed:text-fg focus-visible:outline-1 focus-visible:outline-primary",
    "disabled:opacity-60 disabled:cursor-default",
    "[&>[data-slot=icon]]:size-4 [&>[data-slot=icon]]:shrink-0"
  ],
  variants: {
    isCurrent: {
      true: "text-fg"
    }
  }
})

interface ItemProps extends LinkProps {
  isCurrent?: boolean
}

const Item = ({ className, isCurrent, ...props }: ItemProps) => {
  const { intent, isCompact } = useNavbar()
  return (
    <Link
      slot="navbar-item"
      aria-current={isCurrent ? "page" : undefined}
      className={cr(className, (className, ...renderProps) =>
        navItemStyles({ ...renderProps, isCurrent, className })
      )}
      {...props}
    >
      {(values) => (
        <>
          {typeof props.children === "function" ? props.children(values) : props.children}

          {(isCurrent || values.isCurrent) && !isCompact && intent !== "floating" && (
            <motion.span
              layoutId="current-indicator"
              className="absolute inset-x-2 bottom-[calc(var(--navbar-height)*-0.33)] h-0.5 rounded-full bg-fg"
            />
          )}
        </>
      )}
    </Link>
  )
}

const Logo = ({ className, ...props }: LinkProps) => {
  return (
    <Link
      className={cn(
        "lg:mr-4 focus:outline-none flex items-center gap-x-2 focus-visible:outline-1 focus-visible:outline-primary px-2 py-4 lg:px-0 lg:py-0 text-fg",
        className
      )}
      {...props}
    />
  )
}

const Flex = ({ className, ...props }: React.ComponentProps<"div">) => {
  return <div className={cn("flex items-center gap-2 sm:gap-3", className)} {...props} />
}

const compactStyles = tv({
  base: "lg:hidden flex peer-has-[[data-intent=floating]]:border bg-tertiary justify-between",
  variants: {
    intent: {
      floating: "border h-12 rounded-lg px-3.5",
      inset: "h-14 px-4",
      navbar: "h-14 border-b px-4"
    }
  }
})

const Compact = ({ className, ...props }: React.ComponentProps<"div">) => {
  const { intent } = useNavbar()
  return <div className={compactStyles({ intent, className })} {...props} />
}

const insetStyles = tv({
  base: "grow",
  variants: {
    intent: {
      floating: "",
      inset:
        "bg-tertiary lg:rounded-lg lg:shadow-sm lg:ring-1 lg:ring-dark/5 lg:dark:ring-light/10",
      navbar: ""
    }
  }
})

const Inset = ({ className, ...props }: React.ComponentProps<"div">) => {
  const { intent } = useNavbar()
  return (
    <main
      data-intent={intent}
      className={cn("flex flex-1 flex-col", intent === "inset" && "pb-2 lg:px-2", className)}
    >
      <div className={insetStyles({ intent, className })}>{props.children}</div>
    </main>
  )
}

Navbar.Nav = Nav
Navbar.Inset = Inset
Navbar.Compact = Compact
Navbar.Flex = Flex
Navbar.Trigger = Trigger
Navbar.Logo = Logo
Navbar.Item = Item
Navbar.Section = Section

export { Navbar }

