  showArrow?: boolean
  style?: React.CSSProperties
  respectScreen?: boolean
  "aria-label"?: DialogProps["aria-label"]
  "aria-labelledby"?: DialogProps["aria-labelledby"]
  className?: string | ((values: { defaultClassName?: string }) => string)
}

const Content = ({
  respectScreen = true,
  children,
