      </Modal>
    </ModalOverlay>
  ) : (
    <PopoverPrimitive
      offset={effectiveOffset}
      {...props}
      className={cr(className, (className, renderProps) =>
        popoverContentStyles({
          ...renderProps,
          className
        })
