    }
  }
})

const ToolbarSeparator = ({ className, ...props }: SeparatorProps) => {
  const { orientation } = React.useContext(ToolbarContext)
  const effectiveOrientation = orientation === "vertical" ? "horizontal" : "vertical"
  return (
