} from "react-aria-components"

import { ColorThumb } from "./color-thumb"

export type ColorWheelProps = Omit<ColorWheelPrimitiveProps, "outerRadius" | "innerRadius">

const ColorWheel = (props: ColorWheelProps) => {
  return (
